import { useEffect, useState, useRef } from "react";
import * as React from 'react';
import { useNavigate } from "react-router-dom";
import axios from "axios";
import './CreateWorkoutPlan.css';

function CreateWorkoutPlan(props) {
    const [title, setTitle] = useState('My Workout');
    const [isEditing, setIsEditing] = useState(false);
    const titleRef = useRef(null);

    const exercises = ['Push Pull Legs', '5 Day Split', 'NSuns Program'];

    useEffect(() => {
        if (isEditing) {
            titleRef.current.focus();
        }
    }, [isEditing]);

    const handleTitleChange = (e) => setTitle(e.target.value);

    const toggleEditing = () => setIsEditing(true);

    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            setIsEditing(false);
            e.preventDefault();
        }
    };

    const handleBlur = () => setIsEditing(false);


    return (
        <div className="display-container">
            <div className="exercise-wrapper">
                <div className="exercise-content align-items-center">
                    {isEditing ? (
                        <input
                            ref={titleRef}
                            type="text"
                            value={title}
                            onClick={(e) => e.target.select()}
                            onChange={handleTitleChange}
                            onBlur={handleBlur}
                            onKeyPress={handleKeyPress}
                            className="title-input"
                        />
                    ) : (
                        <h1 className="workout-title" onClick={toggleEditing} style={{ marginTop: "1rem" }}>{title}</h1>
                    )}
                    <div className="login">PPL</div>
                </div>
            </div>
        </div>
    );
}

export default CreateWorkoutPlan;
