import { useEffect } from "react";
import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Exercise from '../pagecreation/Exercise';
import './CreateWorkout.css';

function CreateWorkout(props) {
    const navigate = useNavigate();
    const [title, setTitle] = useState('My Workout');
    const [isEditing, setIsEditing] = useState(false);
    const [exercises, setExercises] = useState([]);
    const titleRef = useRef(null);

    useEffect(() => {
        // Automatically focus the input when it becomes visible
        if (isEditing) {
            titleRef.current.focus();
        }
    }, [isEditing]);

    const handleTitleChange = (e) => {
        setTitle(e.target.value);
    };

    const toggleEditing = () => {
        setIsEditing(true);
    };

    const handleKeyPress = (e) => {
        // Save the title on Enter key and stop editing
        if (e.key === 'Enter') {
            setIsEditing(false);
            e.preventDefault(); // Prevents form submission or any default action
        }
    };

    const handleBlur = () => {
        // Stop editing when the input loses focus
        setIsEditing(false);
    };
    const handleAddExercise = () => {
        setExercises([...exercises, <Exercise key={exercises.length} />]);
    };

    return (
        <div className="display-container">
            <h1 onClick={onHandleChangeTitle}>{title}</h1>
            {exercises.map((exercise, index) => (
                <React.Fragment key={index}>{exercise}</React.Fragment>
            ))}
            <button onClick={handleAddExercise}>New Exercise</button>
        </div>
    );
};

export default CreateWorkout;
