import React from 'react';
import './Posts.css';

function Posts(props) {
    // Sample data for posts
    const posts = [
        {
            username: "John Do",
            date: new Date("2023-04-10"),
            content: "This is the first test post content.",
            profileImg: "/profile.png",
            comments: [
                { username: "JaneDoe", comment: "Nice post!", profileImg: "/profile.png" },
                { username: "MikeSmith", comment: "Thanks for sharing!", profileImg: "/profile.png" },
            ]
        },
        {
            username: "Jane Do",
            date: new Date("2023-04-11"),
            content: "Here's another interesting post.",
            profileImg: "/profile.png",
            comments: [
                { username: "JohnDoe", comment: "I agree with this!", profileImg: "/profile.png" }
            ]
        }
    ];

    return (
        <div className="display-container">
            {posts.map((post, index) => (
                <div>
                    <div key={index} className="post-wrapper">
                        <div className="post-content">
                            <div className="post-top d-flex align-items-center" style={{ background: '#F58528' }}>
                                <div className="posts-profile-image-container">
                                    <img src={post.profileImg} alt="Profile" className="posts-profile-img" />
                                </div>
                                <h3 style={{ color: 'white', fontWeight: '700', marginLeft: '1rem' }}>{post.username}</h3>
                            </div>
                            <div className="post-bottom">
                                <p style={{ fontStyle: 'italic', color: '#777' }}>{post.content}</p>
                                <div style={{ textAlign: 'right', color: '#777', fontWeight: 'bold' }}>
                                    {post.date.toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' })}
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="comments">
                        {post.comments.map((comment, idx) => (
                            <div key={idx} className={`comment ${idx === post.comments.length - 1 ? ' d-flex align-items-center last-comment' : ' d-flex align-items-center'}`}>
                                <div className="posts-profile-image-container-small">
                                    <img src="/profile.png" alt="Profile" className="posts-profile-img-small" />
                                </div>
                                <p style={{ fontStyle: 'italic', color: '#777' }}>{comment.comment}</p>
                            </div>
                        ))}
                    </div>
                </div>
            ))}
        </div>
    );
}

export default Posts;
