import React, { useState } from 'react';
import './Profile.css';

function Profile(props) {
    // Initial state setup for user details
    const [user, setUser] = useState({
        username: "John Do",
        deadlift: "300",
        squat: "250",
        ohp: "120",
        bench: "200",
        friend: "true"
    });
    const [isEditing, setIsEditing] = useState({
        username: false,
        deadlift: false,
        squat: false,
        ohp: false,
        bench: false
    });

    // Function to handle field changes
    const handleChange = (field, value) => {
        setUser(prev => ({ ...prev, [field]: value }));
    };

    // Function to toggle editing state
    const toggleEdit = (field) => {
        setIsEditing(prev => ({ ...prev, [field]: !prev[field] }));
    };

    return (
        <div className="display-container mt-40 d-flex">
            <div className="row">
                <div className="col-md-6 d-flex flex-column align-items-center justify-content-center">
                    <div className="profile-image-container">
                        <img src="/profile.png" alt="Profile" className="profile-img mb-3" />
                    </div>
                    {isEditing.username ? (
                        <input
                            type="text"
                            value={user.username}
                            onChange={(e) => handleChange('username', e.target.value)}
                            onBlur={() => toggleEdit('username')}
                            className="username-input"
                            autoFocus
                        />
                    ) : (
                        <div className="username" onClick={() => toggleEdit('username')}>{user.username}</div>
                    )}
                </div>
                <div className="col-md-6 d-flex flex-column justify-content-center align-items-center">
                    <div className="vr" style={{ height: '100%' }}></div>
                    <div className="right-content d-flex flex-column align-items-center">
                        {['deadlift', 'squat', 'ohp', 'bench'].map((exercise) => (
                            <table key={exercise} className="table mb-3">
                                <thead>
                                    <tr>
                                        <th>{exercise.charAt(0).toUpperCase() + exercise.slice(1)}</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    <tr>
                                        <td>
                                            {isEditing[exercise] ? (
                                                <input
                                                    type="text"
                                                    value={user[exercise]}
                                                    onChange={(e) => handleChange(exercise, e.target.value)}
                                                    onBlur={() => toggleEdit(exercise)}
                                                    className="exercise-input"
                                                    autoFocus
                                                />
                                            ) : (
                                                <span onClick={() => toggleEdit(exercise)}>{user[exercise]} kg</span>
                                            )}
                                        </td>
                                    </tr>
                                </tbody>
                            </table>
                        ))}
                        <button className="login">Day 1 - Push</button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Profile;
