import React, { useState } from 'react';
import "./PageCreation.css";

function Exercise({ name, onNameChange }) {
  const [isEditing, setIsEditing] = useState(false);
  const [localName, setLocalName] = useState(name);
  const [sets, setSets] = useState([1]);

  const handleBlur = () => {
    setIsEditing(false);
    onNameChange(localName);
  };

  const handleChange = (e) => {
    setLocalName(e.target.value);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleBlur();
    }
  };

  const handleAddSet = () => {
    const newSet = {
      id: sets.length + 1,
      name: `Exercise ${sets.length + 1}`
    };
    setSets([...sets, newSet]);
  };

  function SetsAndReps() {
    return (
      <div>
        Set&nbsp;
        {sets.map((set) => (
          set.id
        ))}
        &nbsp;-&nbsp;
        <input className="reps-input"></input>
        &nbsp;reps
      </div>
    );
  }

  return (
    <div className="exercise-wrapper">
      {isEditing ? (
        <input
          type="text"
          value={localName}
          onClick={(e) => e.target.select()}
          onChange={handleChange}
          onBlur={handleBlur}
          onKeyPress={handleKeyPress}
          className="exercise-input"
          autoFocus
        />
      ) : (
        <h2 onClick={() => setIsEditing(true)}>{localName}</h2>
      )}
      <SetsAndReps
        key={sets.id}
        name={sets.name}
      />
      <button className="login" onClick={handleAddSet}>Add Set</button>
    </div>
  );
}

export default Exercise;
