import { useEffect } from "react";
import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Exercise from '../pagecreation/Exercise';
import './CreateWorkout.css';

function CreateWorkout(props) {
    const navigate = useNavigate();
    const [title, setTitle] = useState('My Workout');
    const [exercises, setExercises] = useState([]);

    const onHandleChangeTitle = () => {
        const newTitle = prompt('Enter new title:', title);
        if (newTitle) setTitle(newTitle);
    };

    const handleAddExercise = () => {
        setExercises([...exercises, <Exercise key={exercises.length} />]);
      };

    return (
        <div className="display-container">
            <h1 onClick={onHandleChangeTitle}>{title}</h1>
            {exercises.map((exercise, index) => (
                <React.Fragment key={index}>{exercise}</React.Fragment>
            ))}
            <button onClick={handleAddExercise}>New Exercise</button>
        </div>
    );
};

export default CreateWorkout;
