import React, { useState, useEffect, useRef } from 'react';
import "./PageCreation.css";

function Exercise({ name, onNameChange }) {
  const [isEditing, setIsEditing] = useState(false);
  const [localName, setLocalName] = useState(name);

  const handleBlur = () => {
    setIsEditing(false);
    onNameChange(localName);
  };

  const handleChange = (e) => {
    setLocalName(e.target.value);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleBlur();
    }
  };

  const inputRef = useRef(null); // Add this line

  useEffect(() => { // Add this useEffect hook
    if (isEditing) {
      inputRef.current.focus();
    }
  }, [isEditing]);

  return (
    <div className="exercise-wrapper">
      <div className="exercise-content">
        {isEditing ? (
          <input
            ref={inputRef} // Assign the ref to the input
            type="text"
            value={localName}
            onChange={handleChange}
            onBlur={handleBlur}
            onKeyPress={handleKeyPress}
            className="exercise-input"
          />
        ) : (
          <h2 className="exercise-header" onClick={() => setIsEditing(true)}>{localName}</h2>
        )}
        <div>
          <SetsRepsWeight />
        </div>
        <button className="newSet" onClick={handleAddSet}>Add Set</button>
      </div>
    </div>
  );
}

export default Exercise;
